SELECT M.id FROM sys_menu M LEFT JOIN sys_menu_name MN ON M.id = MN.menu_id WHERE M.logic_flag = 0 AND M.status = 0 AND MN.lang = #{lang}
AND MN.name LIKE CONCAT('%', #{qw.name}, '%')
ORDER BY M.sort
